import React from "react";
import PopupWithForm from "./PopupWithForm";


function ConfirmDeletePopup({ isOpen, onClose, onSubmit, card, onLoading }) {

    const handleConfirmiation = (event) => {
        event.preventDefault();
        onSubmit(card);
    };


    return (
        <PopupWithForm name='confirm' title='Обновить автар' isOpen={isOpen}
            buttonText={onLoading ? 'Ща все будет...' : 'Сохранить'}
            onClose={onClose} onSubmit={handleSubmit} children={<>
                <input

                    type="url"
                    className="popup__form-item popup__form-item_type_link"
                    name="link"
                    id="link-input"
                    aria-label="Введите ссылку на изображение"
                    placeholder="Ссылка на автар"
                    required
                    ref={avatarRef}
                />

                <span className="link-input-error popup__form-item-error"></span>


            </>} />


        // <div className={`popup popup_type_confirm ${isOpen ? 'popup_opened' : ''}`}>
        //     <div className="popup__content">
        //         <button
        //             className="popup__close-button popup__close-button_place_confirm"
        //             type="button"
        //             aria-label="Закрыть"
        //             onClick={onClose}
        //         ></button>
        //         <h2 className="popup__title popup__title_place_confirm">Вы уверены?</h2>
        //         <form
        //             id="confirm-delete-popup"
        //             name="popup"
        //             action="/apply/"
        //             method="POST"
        //             className="popup__form popup__form_type_confirm"
        //             noValidate
        //             onSubmit={handleConfirmiation}
        //         >
        //             <button
        //                 type="submit"
        //                 className="popup__form-button popup__form-button_action_confirm"
        //             >
        //                 {onLoading ? 'Скоро...' : 'Да'}
        //             </button>
        //         </form>
        //     </div>
        // </div>

    );
}

export default ConfirmDeletePopup;
