import React from "react";
import PopupWithForm from "./PopupWithForm";


function ConfirmDeletePopup({ isOpen, onClose, onSubmit, card, onLoading }) {

    const handleConfirmiation = (event) => {
        event.preventDefault();
        onSubmit(card);
    };


    return (
        <PopupWithForm name='edit' title='Редактировать профиль' isOpen={isOpen} onSubmit={handleSubmit} onClose={onClose} buttonText={onLoading ? 'Ща все будет...' : 'Сохранить'} children={
            <>
                <input

                    id="firstname-input"
                    placeholder="Имя"
                    type="text"
                    className="popup__form-item popup__form-item_type_name"
                    name="firstname"
                    minLength="2"
                    maxLength="40"
                    value={name || ""}
                    onChange={handleNameChange}
                    required

                />
                <span className="firstname-input-error popup__form-item-error"></span>

                <input

                    id="info-input"
                    placeholder="Профессиональная деятельность"
                    type="text"
                    className="popup__form-item popup__form-item_type_job"
                    name="info"
                    value={description || ""}
                    onChange={handleDescriptionChange}
                    minLength="2"
                    maxLength="200"
                    required
                />
                <span className="info-input-error popup__form-item-error"></span>
            </>} />

        // <div className={`popup popup_type_confirm ${isOpen ? 'popup_opened' : ''}`}>
        //     <div className="popup__content">
        //         <button
        //             className="popup__close-button popup__close-button_place_confirm"
