import React from "react";


function Main() {
    function handleEditAvatarClick() {
        document.querySelector('.popup_type_edit-avatar').classList.add('popup_opened')
    }

    function handleEditProfileClick() {
        document.querySelector('.popup_type_edit').classList.add('popup_opened')
    }

    function handleAddPlaceClick() {
        document.querySelector('.popup_type_add-image').classList.add('popup_opened')
    }

    return (
        <div className="page">
            <main className="content">
                <section className="profile">
                    <div className="profile__image-edit">
                        <img
                            src="<%=require('./images/Jak-Iv.jpg')%>"
                            alt="Картинка профиля"
                            className="profile__image"
                        />
                        <button
                            title="Загрузить новый аватар"
                            className="profile__image-edit-button"
                            onClick={handleEditAvatarClick}
                        ></button>
                    </div>
                    <div className="profile__info">
                        <h1 className="profile__name"></h1>
                        <button
                            className="profile__button"
                            type="button"
                            aria-label="Редактировать профиль"
                            onClick={handleEditProfileClick}
                        ></button>
                        <p className="profile__occupation"></p>
                    </div>
                    <button
                        className="profile__add-button"
                        type="button"
                        aria-label="Добавить пост"
                    ></button>
                </section>

                <section className="elements"></section>
            </main>
        </div>
    );
}

export default Main;
