import React from "react";
import PopupWithForm from "./PopupWithForm";
import { useForm } from 'react-hook-form';


function AddPlacePopup({ isOpen, onClose, onAddPlace, onLoading }) {
    const [name, setName] = React.useState('');
    const [link, setLink] = React.useState('');
    const {
        register,
        formState: {
            errors,
        },
        handleSubmit
    } = useForm({ mode: 'onBlur' });

    function handleNameChange(e) {
        setName(e.target.value);
    };

    function handleLinkChange(e) {
        setLink(e.target.value);
    };

    function onSubmit(e) {
        // e.preventDefault();

        onAddPlace({
            name: name,
            link: link
        });
    };


    return (
        <PopupWithForm name='add-image' title='Новое место' isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit} children={<>
            <input
                {...register('name', {
                    required: 'Вы пропустили это поле',
                    minLength: {
                        value: 2,
                        message: 'Минимальное количество символов: 2. Длина текста сейчас: 1 символ.'
                    }
                })}
                type="text"
                className="popup__form-item popup__form-item_type_title"
                aria-label="Введите заголовок для изображения"
                name="name"
                id="title-input"
                placeholder="Название"
                maxLength="30"
                value={name}
                onChange={handleNameChange}
                required

            />
            <span className="title-input-error popup__form-item-error">{errors?.name?.message}</span>

            <input
                type="url"
                className="popup__form-item popup__form-item_type_link"
                name="link"
                id="avatar-link-input"
                aria-label="Введите ссылку на изображение"
                placeholder="Ссылка на картинку"
                value={link}
                onChange={handleLinkChange}
                required

            />
            <span className="avatar-link-input-error popup__form-item-error"></span>

            <button
                type="submit"
                className="popup__form-button popup__form-button_action_add"
            >
                {onLoading ? 'Ван секонд...' : 'Создать'}
            </button>
        </>} />

    );
}

export default AddPlacePopup;
