import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import * as auth from '../utils/auth';


function Login({ handleLogin }) {
    const initialData = {
        email: '',
        password: '',
    }

    const [profileData, setProfileData] = useState(initialData);

    const handleChange = (e) => {
        const { name, value } = e.target
        setProfileData(profileData => ({
            ...profileData,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault()

        if (!profileData.password || !profileData.email) {
            return;
        }
        auth.authorize(email, password)
            .then((data) => {
                if (data.jwt) {
                    this.setState({ email: '', password: '' }, () => {
                        handleLogin();
                        history.push('/');
                    })
                }
            })
            .catch(err => console.log(err)); // запускается, если пользователь не найден
    }
    return (

        <form className="login" onSubmit={handleSubmit}>
            <h1 className="login__title">Вход</h1>
            <input className="login-input__email" placeholder="Email" onChange={handleChange}></input>
            <input className="login-input__password" placeholder="Пароль" onChange={handleChange}></input>
            <button className="login__button" type="submit">Войти</button>
        </form>

    );
}


export default withRouter(Login);


