import React from "react";
import api from '../utils/Api';


function Main({ onEditProfile, onAddPlace, onEditAvatar }) {
    const [userName, setUserName] = React.useState();
    const [userDescription, setUserDescription] = React.useState();
    const [userAvatar, setUserAvatar] = React.useState();
    const [cards, setCards] = React.useState([])

    React.useEffect(() => {
        api.getUserInfo()
            .then((res) => {
                setUserName(res.name);
                setUserDescription(res.about);
                setUserAvatar(res.avatar);
            })
    }, [])

    React.useEffect(() => {
        api.getInitialCards()
            .then((res) => {
                setCards([res]);
            })
    }, [])



    return (
        <main className="content">
            <section className="profile">
                <div className="profile__image-edit">
                    <img
                        src={userAvatar}
                        alt="Картинка профиля"
                        className="profile__image"
                    />
                    <button
                        title="Загрузить новый аватар"
                        className="profile__image-edit-button"
                        onClick={onEditAvatar}
                    ></button>
                </div>
                <div className="profile__info">
                    <h1 className="profile__name">{userName}</h1>
                    <button
                        className="profile__button"
                        type="button"
                        aria-label="Редактировать профиль"
                        onClick={onEditProfile}
                    ></button>
                    <p className="profile__occupation">{userDescription}</p>
                </div>
                <button
                    className="profile__add-button"
                    type="button"
                    aria-label="Добавить пост"
                    onClick={onAddPlace}
                ></button>
            </section>

            <section className="elements">
                {cards.map((card) => {
                    return (<article className="element">
                        <img src={card.link} className="element__image" />
                        <button
                            className="element__trash-button"
                            type="button"
                            aria-label="Удалить"
                        ></button>
                        <div className="element__bottom">
                            <h2 className="element__title">{card.name}</h2>
                            <div className="element__like-container">
                                <button
                                    className="element__like-button"
                                    type="button"
                                    aria-label="Поставить лайк"
                                ></button>
                                <span className="element__like-count">0</span>
                            </div>
                        </div>
                    </article>)
                })}
            </section>
        </main>

    );
}

export default Main;
