import React from "react";
import PopupWithForm from "./PopupWithForm";
import { useForm } from 'react-hook-form';

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar, onLoading }) {

    const avatarRef = React.useRef();

    function onSubmit(e) {
        // e.preventDefault();

        onUpdateAvatar({
            avatar: avatarRef.current.value,
        });
    };

    const {
        register,
        formState: {
            errors
        },
        handleSubmit,
        reset
    } = useForm({ mode: 'onChange' });

    return (

        <PopupWithForm name='confirm' title='Обновить автар' isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit(onSubmit)} children={<>
            <input
                {...register('link', {
                    required: 'Вы пропустили это поле',

                })}
                // type="url"
                className="popup__form-item popup__form-item_type_link"
                name="link"
                id="link-input"
                aria-label="Введите ссылку на изображение"
                placeholder="Ссылка на автар"
                // required
                ref={avatarRef}
            />
            debugger
            <span className="link-input-error popup__form-item-error">{errors?.link?.message}</span>

            <button
                type="submit"
                className="popup__form-button popup__form-button_action_add"

            >
                {onLoading ? 'Ну-кааааа...' : 'Создать'}
            </button>
        </>} />

    );
}

export default EditAvatarPopup;
